jQuery(document).ready(function(){var product;jQuery(document.body).on("click",".remove-product",function(){var a=jQuery(this).parent('li');product=a.attr('class');a.remove();jQuery.ajax({url:'customblocks/index/removepro',type:'POST',dataType:'json',data:{product:product},success:function(data){console.log(data);if(data.status=='empty'){jQuery('.compare-section').fadeOut('slow');}}})});jQuery(document.body).on("click",".remove-compare",function(){jQuery('.compare-section').fadeOut('slow');});});function ajaxCompare(url,id){var product_id='#products_'+id;url=url.replace("catalog/product_compare/add","customblocks/index/compare");url+='isAjax/1/';jQuery('.opc-ajax-loader').css('display','block');jQuery.ajax({url:url,dataType:'json',success:function(data){jQuery('.opc-ajax-loader').css('display','none');if(data.status=='ERROR'){alert(data.message);}else if(data.status=='SameCatError'){jQuery(product_id).attr('checked',false);alert(data.message);}else{if(jQuery('.ajaxcompare').length){jQuery('.ajaxcompare').replaceWith(data.sidebar);jQuery(".compare-section").addClass('compare-fixed');}}}});}